mapper.insert:
INSERT INTO board.bbs
(id, name, email, pass, title, comment, wdate, ip, see)
VALUES(
(SELECT MAX 
FROM (SELECT MAX(id)+1 As 'MAX' FROM bbs) AS getMax
),
 #{name}, #{email}, #{pass},#{title}, #{comment}, NOW(), #{ip}, 0
);

mapper.update:
UPDATE bbs 
SET
title = #{title},
comment = #{comment}
WHERE id=#{id}
